import React from 'react';
import Image from './Image';
import './App.css';




class App extends React.Component {

  state = {
    order: false,
    result: "",
    img1: "",
    img2: "",
  };

  componentDidMount = () => {
    this.chooseImage();
  };

  chooseOrder = () => {
    this.setState({order: Math.random() > 0.5});
  };

  displayResult = (right) => {
    if (right) {
      this.setState({result: "Correct!"});
    }
    else {
      this.setState({result: "Not correct!"});
    }
  };

  chooseImage = () => {
    if (order) {
      let num = Math.floor(Math.random() * 203) + 7;
      this.setState({img: "./real_images/res" + String(num)+ ".png"});
    }
    else if (this.props.type === "fake") {
      let num = Math.floor(Math.random() * 250);
      this.setState({img: "./fake_images/res" + String(num)+ ".jpg"});
    }
  };

  reset = () => {
    this.chooseOrder();

  };


  render () {
    return (
      <div className="App">
        <header className="App-header">

          <p>
            {this.state.result}
          </p>

          <Image type={this.state.order ? "fake" : "real"} displayResult={this.displayResult} reset={this.reset}></Image>
          <Image type={this.state.order ? "real" : "fake"} displayResult={this.displayResult} reset={this.reset}></Image>


          <p>
            Which one is the <b>fake</b> person?
          </p>


        </header>
      </div>
    );
  }

}

export default App;
