import React from 'react';


class Image extends React.Component {

  state = {
    img: "",
  };

  componentDidMount = () => {
    this.chooseImage();
  };



  handleClick = () => {
    this.props.displayResult(this.props.type === "fake");
    this.props.reset();
    this.chooseImage();
    console.log("hey");
  };


  render() {
    return (
      <img src={this.state.img} onClick={this.handleClick} alt="image" />
    );
  }
  }

  export default Image;
